import React from 'react'
import logo from '../logo.svg';
import tiles1 from '../tiles1.svg';
import styles from '../Cssstyles/FormStyles.module.css';

export default function ProductCatalog() {

    const tiles = {
        id: number,
        name: string,
        price: number,
        size:string,
        image: string,
        quantity: number,
      };

      const [isLoading, setIsLoading] = useState(true)
  const [error, setError] = useState(false)
  const [cart, setCart] = useLocalStorageState<CartProps>('cart', {})

    const tilesSample = [
        { id: 1, name: 'Amo Brown', price: 60 , size: '600x600 mm ft'},
        { id: 2, name: 'Cibola Wood', price: 25 , size: '1200x600 mm ft'},
        { id: 3, name: 'Ecowood Multi', price: 45 , size: '450x300 mm ft'},
        { id: 4, name: 'Garden Green', price: 50 , size: '300x300 mm ft'},
        { id: 5, name: 'Mosaic Art', price: 70 , size: '300x600 mm ft'},
    ];

    const rowStyle ={
        border: '1px solid black',
        textAlign:'center',
        width: 200
    };

    const listOfTiles = tilesSample.map(tiles=> 
        <tr key={tiles.id} > 
            <td style={rowStyle}><img src={tiles1} /></td>
            <td style={rowStyle}>{tiles.name}</td>
            <td style={rowStyle}>{tiles.price}</td>
            <td style={rowStyle}>{tiles.size}</td>
            <td style={rowStyle}><button>Add to Cart</button></td>
        </tr>
    );

    const addToCart = (product) => {
        product.quantity = 1
    
        setCart((prevCart) => ({
          ...prevCart,
          [product.id]: product,
        }))
      }
    

  return (
    <div>
        <h3>List of Tiles</h3>
        <CatalogueHeader > </CatalogueHeader>

        <div className={styles.container}>
        {tilesSample.map(product => (
          <div className={styles.product} key={product.id}>
            <img src={logo} alt={product.name} />
            <h3>{product.name}</h3>
            <p>Price: <CurrencyFormatter amount={product.price} /></p>
            <button onClick={() => addToCart(product)}>Add to Cart</button>
          </div>
        ))}
      </div>

        {/* <table>
            <thead>
                <tr>
                    <th style={rowStyle}>Image</th>
                    <th style={rowStyle}>Name</th>
                    <th style={rowStyle}>Price</th>
                    <th style={rowStyle}> Size</th>
                    <th style={rowStyle}>Add</th>
                </tr>
            </thead>
            <tbody>
                {listOfTiles}
            </tbody>
        </table> */}
    </div>
  )
}
